CREATE TABLE contacto (id BIGSERIAL, nombre VARCHAR(255) NOT NULL, ap_paterno VARCHAR(255) NOT NULL, ap_materno VARCHAR(255), telefono VARCHAR(20) NOT NULL, email VARCHAR(100) NOT NULL, direccion VARCHAR(255), empresa VARCHAR(255), created_at TIMESTAMP NOT NULL, updated_at TIMESTAMP NOT NULL, PRIMARY KEY(id));
CREATE TABLE noticia (id BIGSERIAL, titulo VARCHAR(255) NOT NULL, cuerpo TEXT NOT NULL, imagen VARCHAR(255) NOT NULL, autor VARCHAR(255) NOT NULL, lugar VARCHAR(255) NOT NULL, fecha DATE NOT NULL, created_at TIMESTAMP NOT NULL, updated_at TIMESTAMP NOT NULL, PRIMARY KEY(id));
CREATE TABLE persona (id BIGSERIAL, nombre VARCHAR(50) NOT NULL, ap_paterno VARCHAR(30) NOT NULL, ap_materno VARCHAR(30), ap_casada VARCHAR(30), ci VARCHAR(14) NOT NULL UNIQUE, expedido VARCHAR(2) NOT NULL, direccion VARCHAR(255) NOT NULL, telefono VARCHAR(20), celular VARCHAR(20) NOT NULL, fax VARCHAR(20), casilla VARCHAR(10), email VARCHAR(255), fecha_nacimiento DATE, is_active BOOLEAN DEFAULT 'true' NOT NULL, foto VARCHAR(50), created_at TIMESTAMP NOT NULL, updated_at TIMESTAMP NOT NULL, PRIMARY KEY(id));
